import { useState, useRef } from 'react';
import VideoPlayer from './VideoPlayer';
import './App.css';

function App() {
  const videoRef= useRef(null);
  const videoQueue = ['https://media.w3.org/2010/05/sintel/trailer_hd.mp4', ]
  const [currentVideo, setCurrentVideo] = useState('https://media.w3.org/2010/05/sintel/trailer_hd.mp4');

  return (
    <div className="App">
      <VideoPlayer src={currentVideo} ref={videoRef}/>
      <div className="docs-example">
          <Form>
            <FormGroup>
              <Label for="inputVideoUrl">Video Url</Label>
              <Input
                name="inputVideoUrl"
                id="inputVideoUrl"
                value={this.state.inputVideoUrl}
                onChange={this.handleValueChange}
              />
            </FormGroup>
            <FormGroup>
              <Button type="button" onClick={this.updatePlayerInfo}>
                Update
              </Button>
            </FormGroup>
          </Form>
        </div>
    </div>
  );
}

export default App;
