import { useState, useRef, useEffect } from 'react';
import VideoPlayer from './VideoPlayer';
import './App.scss';

function App() {
  const videoRef= useRef(null);
  const videoQueue = ['https://media.w3.org/2010/05/sintel/trailer_hd.mp4', 'http://www.w3schools.com/html/mov_bbb.mp4'];
  const [currentVideo, setCurrentVideo] = useState(videoQueue[0]);
  const [testData, updateTestData] = useState('');
  const getData = () => {
    const foo = videoRef.current.getState().player;
    console.log(foo);
    updateTestData(foo);
  };
  useEffect(() => {
    const timer = setInterval(() => {
      getData();
    }, 100);
               // clearing interval
    return () => clearInterval(timer);
  });



  return (
    <div className="App">
      <div className="VideoPlayer">
        <VideoPlayer src={currentVideo} videoRef={videoRef}/>
      </div>
      <div className="TestData">
        <table>
          <thead>
            <td>key</td>
          </thead>
        {Object.keys(testData).map(key => {
          return <td key={key}>{key}: </td>;
        })}
        </table>
      </div>
    </div>
  );
}

export default App;
