import { useState, useRef, useEffect } from 'react';
import VideoPlayer from './VideoPlayer';
import './App.scss';

function App() {
  const videoRef= useRef(null);
  const videoList = ['https://media.w3.org/2010/05/sintel/trailer_hd.mp4', 'http://www.w3schools.com/html/mov_bbb.mp4'];
  const [currentVideo, setCurrentVideo] = useState(0);
  const [testData, updateTestData] = useState('');

  const getData = () => {
    const foo = videoRef.current.getState().player;
    updateTestData(foo);
    if (testData.ended) {
      setCurrentVideo(currentVideo === 0 ? 1 : 0)
      videoRef.current.load();
    }
  };

  useEffect(() => {
    const timer = setInterval(() => {
      getData();
      if (testData.ended) {
        clearInterval(timer);
        setCurrentVideo(currentVideo === 0 ? 1 : 0)
        videoRef.current.load();
      }
    }, 500);

    return () => clearInterval(timer);
  });



  return (
    <div className="App">
      <div className="VideoPlayer">
        <VideoPlayer src={videoList[currentVideo]} videoRef={videoRef}/>
      </div>
      <div className="TestData">
        <table>
          <tr>
            <th>key</th>
            <th>value</th>
          </tr>
        {Object.keys(testData).map(key => {
          const value = testData[key];
          if (typeof value !== "object"){
            return (<tr key={key}>
              <td>{key}</td>
              <td>{testData[key]}</td>
              </tr>
            );
          }
        })}
        </table>
      </div>
    </div>
  );
}

export default App;
